#Author:Bing Liuimport osdef arrangeFile(pathInput, pathOutput):    txtNameList = os.listdir(pathInput)  # 只显示该目录下的文件名和目录名，不包含子目录中的文件，默认为当前文件所在目录    for txtName in txtNameList:        f1 = open(pathInput + "\\" + txtName, mode='r', encoding='UTF-8')  # 返回一个文件对象        txt = f1.read()  # 调用文件的 readline()方法        txtContentList = txt.split('\n')        for str1 in txtContentList:            dataList1 = str1.split(", ")            for str2 in dataList1:                dataList2 = str2.split(" = ")                if len(dataList2) == 2:                    f2 = open(pathOutput + "\\" + dataList2[0] + ".txt", 'a')                    f2.write(dataList2[1] + ',')                    f2.close()        f1.close()        print(txtName + "文件处理完毕")def minMax(pathOutput, pathMinMax):    txtNameList = os.listdir(pathOutput)  # 只显示该目录下的文件名和目录名，不包含子目录中的文件，默认为当前文件所在目录    for txtName in txtNameList:        f1 = open(pathOutput + "\\" + txtName, mode='r', encoding='UTF-8')  # 返回一个文件对象        txt = f1.read()  # 调用文件的 readline()方法        txtContentList = txt.split(',')        txtContentList.remove('')        intlist = [int(x) for x in txtContentList]        dataMax =str(max(intlist))        dataMin =str(min(intlist))        f1.close()        print(txtName+"：max="+dataMax+" min="+dataMin)        f2 = open(pathMinMax + "\\MinAndMax.txt", mode='a', encoding='UTF-8')        f2.write(txtName+"：max="+dataMax+" min="+dataMin+' , \n')        f2.close()if __name__ == '__main__':    pathInput = "C:\\Users\\liubing\\PycharmProjects\\autoTXT\\data\\input"    pathOutput = "C:\\Users\\liubing\\PycharmProjects\\autoTXT\\data\\output"    pathMinMax = "C:\\Users\\liubing\\PycharmProjects\\autoTXT\\data\\MinMax"    # arrangeFile(pathInput, pathOutput)    minMax(pathOutput, pathMinMax)